window.onload=(()=>{new Controller});class Controller{constructor(){const e=new View,o=document.querySelector("#peopleList");this.loadPeople(2,e,o)}loadPeople(e,o,r){fetch(`https://randomuser.me/api/?nat=nl&inc=picture,name,phone,cell,email&results=${e}`).then(e=>e.json()).then(e=>{o.showPeopleList(r,e.results)}).catch(e=>console.error(e))}}class Person{constructor(e,o,r){const n=document.querySelector("#personTemplate").cloneNode(!0);return n.classList.remove("hidden"),n.querySelector(".person__image").src=e,n.querySelector(".person__name").innerText=o,n.querySelector(".person__phone-number").innerText=r,n}}class View{constructor(){}showPeopleList(e,o){o.forEach(o=>{e.appendChild(new Person(o.picture.large,`${o.name.first} ${o.name.last}`,o.phone))})}}